import React, { useState } from "react";
import { useEffect } from 'react';
import './Slider1.scss';



const Slider = () => {
    const slides = [
        {
          id: 1,
          image: 'https://picsum.photos/640/480?random=1',
          title: 'Slide 1',
          description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.'
        },
        {
          id: 2,
          image: 'https://picsum.photos/640/480?random=2',
          title: 'Slide 2',
          description: 'Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.'
        },
        {
          id: 3,
          image: 'https://picsum.photos/640/480?random=3',
          title: 'Slide 3',
          description: 'Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.'
        }
      ];

  const [currentSlide, setCurrentSlide] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide(currentSlide === slides.length - 1 ? 0 : currentSlide + 1);
    }, 3000);
    return () => clearInterval(interval);
  }, [currentSlide]);

  const handleDotClick = (index) => {
    setCurrentSlide(index);
  };

  return (
    <div className="slider">
      <div className="slider__slides-container">
        {slides.map((slide, index) => (
          <div key={slide.id} className={`slider__slide ${index === currentSlide ? 'active' : ''}`}>
            <img src={slide.image} alt={slide.title} />
            <div className="slider__slide-content">
              <h2>{slide.title}</h2>
              <p>{slide.description}</p>
            </div>
          </div>
        ))}
      </div>
      <div className="slider__dots">
        {slides.map((slide, index) => (
          <button
            key={slide.id}
            className={`slider__dot ${index === currentSlide ? 'active' : ''}`}
            onClick={() => handleDotClick(index)}
          ></button>
        ))}
      </div>
    </div>
  );
};

export default Slider;
